importScripts('https://storage.googleapis.com/workbox-cdn/releases/6.5.4/workbox-sw.js');

workbox.setConfig({ debug: false });

// Precache all assets generated by Vite
workbox.precaching.precacheAndRoute([
  { url: '/', revision: '1' },
  { url: '/index.html', revision: '1' },
  // Use a glob pattern or dynamic import to match Vite's hashed files
  { url: '/assets/index-*.js', revision: null },
  { url: '/assets/index-*.css', revision: null },
], {
  ignoreURLParametersMatching: [/[hash]/], // Ignore query params in hashes
});

// Cache audio files (Blob URLs aren't cacheable directly, handle via app logic)
workbox.routing.registerRoute(
  ({ request }) => request.destination === 'audio',
  new workbox.strategies.CacheFirst({
    cacheName: 'audio-cache',
    plugins: [
      new workbox.expiration.ExpirationPlugin({
        maxEntries: 50,
        maxAgeSeconds: 30 * 24 * 60 * 60,
      }),
    ],
  })
);

// Cache lyrics (adjust URL pattern based on your app's API or storage)
workbox.routing.registerRoute(
  ({ url }) => url.pathname.endsWith('.lrc'),
  new workbox.strategies.StaleWhileRevalidate({
    cacheName: 'lyrics-cache',
    plugins: [
      new workbox.expiration.ExpirationPlugin({
        maxEntries: 100,
        maxAgeSeconds: 7 * 24 * 60 * 60,
      }),
    ],
  })
);

// Fallback for offline
workbox.routing.setCatchHandler(({ event }) => {
  if (event.request.destination === 'document') {
    return caches.match('/index.html');
  }
  return Response.error();
});